(23, 1, '2025-09-26 07:13:22', '2025-09-26 07:13:22', '{\n    \"widget_block[11]\": {\n        \"value\": {\n            \"raw_instance\": {\n                \"content\": \"<!-- wp:details -->\\n<details class=\\\"wp-block-details\\\"><summary>Danh m\\u1ee5c</summary><!-- wp:categories /-->\\n\\n<!-- wp:details -->\\n<details class=\\\"wp-block-details\\\"><summary></summary></details>\\n<!-- /wp:details --></details>\\n<!-- /wp:details -->\"\n            }\n        },\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2025-09-26 07:13:22\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '117955f5-dcfa-4a49-a6a4-9a33b200cf2a', '', '', '2025-09-26 07:13:22', '2025-09-26 07:13:22', '', 0, 'http://localhost/wordpress-6.8.2/wordpress/2025/09/26/117955f5-dcfa-4a49-a6a4-9a33b200cf2a/', 0, 'customize_changeset', '', 0),
